(function() {
	$('span.checkbox').click(function(){
		if($(this).hasClass('checked')){
			$(this).removeClass('checked');
			$(this).find('input').attr('checked',false);
		}else{
			$(this).addClass('checked');
			$(this).find('input').attr('checked',true);
		}
	});
}());


